mapper.select:
		select `id`,`bizdate`,`forwunit`,`carnum`,`station`,`tonnage`,`settletonnage`,
		       `adjusttonnage`,`transcost`,`inforfee`,`colmethod`,`transfee`,`riskcues`,
		       `fulltrans`,`fulpaydate`,`bankcard`,`bankpaydate`,`bankcardtwo`,`bankpaytwodate`,
		       `fultranstwo`,`fulpaytwodate`,`acbalance`,`receipt`,`receiptdate`,`carrier`,`remark`
		,order_no,pay_type,kpid,dept_id,updated_name,shoushufei,shoushufeidate
		from gc_kehu



		  		   and bizdate >= #{startDate} 
			       and bizdate <= #{endDate} 


					   and carnum like CONCAT('%',#{carnum},'%') 



					   and station like CONCAT('%',#{station},'%') 



				   and receipt = #{receipt} 
				   and receipt in('有','预有') 



				 and receiptdate >= #{tstartDate} 
				 and receiptdate <= #{tendDate} 




				  	 and carrier like CONCAT('%',#{carrier},'%') 


				 and dept_id = #{deptId} 





                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from gc_kehu



	 and bizdate >= #{startDate} 
	 and bizdate <= #{endDate} 


		 and carnum like CONCAT('%',#{carnum},'%') 



		 and station like CONCAT('%',#{station},'%') 



	 and receipt = #{receipt} 
	 and receipt in('有','预有') 



	 and receiptdate >= #{tstartDate} 
	 and receiptdate <= #{tendDate} 




		 and carrier like CONCAT('%',#{carrier},'%') 


	 and dept_id = #{deptId}

select LEFT(#{startDate},7) as bizMonth, sum(acbalance) as acbalance,sum(fulltrans) as fulltrans,sum(bankcard) as bankcard, sum(xianjin) as xianjin,sum(shiyou) as shiyou,sum(shihua) as shihua,sum(jingbo) as jingbo, sum(qichu) as qichu,sum(shoushufei) as shoushufei from (
-- 当天回款金额(transfee) select sum(transfee) as acbalance,0 as fulltrans,0 as bankcard,0 as xianjin,0 as shiyou,0 as shihua,0 as jingbo,0 as qichu,0 as shoushufei from gc_kehu where receipt in ('预有','有')
and bizdate >= #{startDate} and bizdate <= #{endDate} and dept_id = #{deptId}
union all
-- 当天手续费金额(shoushufei) select 0 as acbalance,0 as fulltrans,0 as bankcard,0 as xianjin,0 as shiyou,0 as shihua,0 as jingbo,0 as qichu,sum(shoushufei) as shoushufei from gc_kehu where 1=1 and bizdate >= #{startDate} and bizdate <= #{endDate} and dept_id = #{deptId}
union all
select 0 as acbalance,sum(fulltrans) as fulltrans,sum(bankcard) as bankcard, sum(xianjin) as xianjin,sum(shiyou) as shiyou,sum(shihua) as shihua,sum(jingbo) as jingbo, 0 as qichu,0 as shoushufei from ( -- 第一次付款金额 select 0 as acbalance, sum(case when pay_type = '运满满' then fulltrans else 0 end) as fulltrans, sum(case when pay_type = '农行卡' then fulltrans else 0 end) as bankcard, sum(case when pay_type = '现金' then fulltrans else 0 end) as xianjin, sum(case when pay_type = '百通石油' then fulltrans else 0 end) as shiyou, sum(case when pay_type = '百通石化' then fulltrans else 0 end) as shihua, sum(case when pay_type = '百通京博' then fulltrans else 0 end) as jingbo, 0 as qichu from gc_kehu where receipt in ('预有','有')
and bizdate >= #{startDate} and bizdate <= #{endDate} and dept_id = #{deptId} group by pay_type
union all
-- 第二次付款金额 select 0 as acbalance, sum(case when forwunit = '运满满' then bankcard else 0 end) as fulltrans, sum(case when forwunit = '农行卡' then bankcard else 0 end) as bankcard, sum(case when forwunit = '现金' then bankcard else 0 end) as xianjin, sum(case when forwunit = '百通石油' then bankcard else 0 end) as shiyou, sum(case when forwunit = '百通石化' then bankcard else 0 end) as shihua, sum(case when forwunit = '百通京博' then bankcard else 0 end) as jingbo, 0 as qichu from gc_kehu where receipt in ('预有','有') and bizdate >= #{startDate} and bizdate <= #{endDate} and dept_id = #{deptId}
group by forwunit
) t
union all
-- 期初余额（等于当月的所有余额累计） select 0 as acbalance,0 as fulltrans,0 as bankcard,0 as xianjin,0 as shiyou,0 as shihua,0 as jingbo, (transfee - fulltrans - bankcard) as qichu,0 as shoushufei from ( select sum(transfee) as transfee, sum(case when bizdate>= #{startDate}and bizdate< #{endDate} then fulltrans else 0 end) as fulltrans, sum(case when bizdate>= #{startDate} and bizdate< #{endDate} then bankcard else 0 end) as bankcard from gc_kehu where receipt in ('预有','有') and bizdate >= #{startDate} and bizdate <= #{endDate} and dept_id = #{deptId} ) t
) t

select sum(acbalance) as acbalance,sum(shoushufei) as shoushufei,sum(fulltrans) as fulltrans,sum(bankcard) as bankcard, sum(xianjin) as xianjin,sum(shiyou) as shiyou,sum(shihua) as shihua,sum(jingbo) as jingbo, sum(qichu) as qichu from (
-- 当天回款金额(transfee) select sum(transfee) as acbalance,0 as fulltrans,0 as bankcard,0 as xianjin,0 as shiyou,0 as shihua,0 as jingbo,0 as qichu,0 as shoushufei from gc_kehu where receipt in ('预有','有') and receiptdate = #{endDate} and dept_id = #{deptId}
union all
-- 当天手续费金额(shoushufei) select 0 as acbalance,0 as fulltrans,0 as bankcard,0 as xianjin,0 as shiyou,0 as shihua,0 as jingbo,0 as qichu,sum(shoushufei) as shoushufei from gc_kehu where receipt in ('预有','有') and shoushufeidate = #{endDate} and dept_id = #{deptId}
union all
select 0 as acbalance,sum(fulltrans) as fulltrans,sum(bankcard) as bankcard, sum(xianjin) as xianjin,sum(shiyou) as shiyou,sum(shihua) as shihua,sum(jingbo) as jingbo, 0 as qichu,0 as shoushufei from ( -- 第一次付款金额 select 0 as acbalance, sum(case when pay_type = '运满满' then fulltrans else 0 end) as fulltrans, sum(case when pay_type = '农行卡' then fulltrans else 0 end) as bankcard, sum(case when pay_type = '现金' then fulltrans else 0 end) as xianjin, sum(case when pay_type = '百通石油' then fulltrans else 0 end) as shiyou, sum(case when pay_type = '百通石化' then fulltrans else 0 end) as shihua, sum(case when pay_type = '百通京博' then fulltrans else 0 end) as jingbo, 0 as qichu from gc_kehu where receipt in ('预有','有') and fulpaydate = #{endDate} and dept_id = #{deptId} group by pay_type
union all
-- 第二次付款金额 select 0 as acbalance, sum(case when forwunit = '运满满' then bankcard else 0 end) as fulltrans, sum(case when forwunit = '农行卡' then bankcard else 0 end) as bankcard, sum(case when forwunit = '现金' then bankcard else 0 end) as xianjin, sum(case when forwunit = '百通石油' then bankcard else 0 end) as shiyou, sum(case when forwunit = '百通石化' then bankcard else 0 end) as shihua, sum(case when forwunit = '百通京博' then bankcard else 0 end) as jingbo, 0 as qichu from gc_kehu where receipt in ('预有','有')
and bankpaydate = #{endDate} and dept_id = #{deptId} group by forwunit
) t
union all -- 期初余额（等于当月的所有余额累计）
select 0 as acbalance,0 as fulltrans,0 as bankcard,0 as xianjin,0 as shiyou,0 as shihua,0 as jingbo, (transfee - fulltrans - bankcard-shoushufei) as qichu,0 as shoushufei from ( select sum(transfee) as transfee, sum(case when shoushufeidate>= #{startDate} and shoushufeidate< #{endDate} then shoushufei else 0 end) as shoushufei, sum(case when fulpaydate>= #{startDate} and fulpaydate< #{endDate} then fulltrans else 0 end) as fulltrans, sum(case when bankpaydate>= #{startDate} and bankpaydate< #{endDate} then bankcard else 0 end) as bankcard from gc_kehu where receipt in ('预有','有') and receiptdate >= #{startDate} and receiptdate < #{endDate} and dept_id = #{deptId} ) t
) t
