mapper.select:
		select `id`,`bizdate`,`forwunit`,`carnum`,`station`,`tonnage`,`settletonnage`,
		       `adjusttonnage`,`transcost`,`inforfee`,`colmethod`,`transfee`,`riskcues`,
		       `fulltrans`,`fulpaydate`,`bankcard`,`bankpaydate`,`bankcardtwo`,`bankpaytwodate`,
		       `fultranstwo`,`fulpaytwodate`,`acbalance`,`receipt`,`receiptdate`,`carrier`,`remark`
		,order_no,pay_type,kpid
		from gc_kehu

		  		   and id = #{id} 

		  		   and bizdate >= #{startDate} 
			       and bizdate <= #{endDate} 


		  		   and forwunit = #{forwunit} 

					   and carnum like CONCAT('%',#{carnum},'%') 

		  		   and station = #{station} 
		  		   and tonnage = #{tonnage} 
		  		   and settletonnage = #{settletonnage} 
		  		   and adjusttonnage = #{adjusttonnage} 
		  		   and transcost = #{transcost} 
		  		   and inforfee = #{inforfee} 
		  		   and colmethod = #{colmethod} 
		  		   and transfee = #{transfee} 
		  		   and riskcues = #{riskcues} 
		  		   and fulltrans = #{fulltrans} 
		  		   and fulpaydate = #{fulpaydate} 
		  		   and bankcard = #{bankcard} 
		  		   and bankpaydate = #{bankpaydate} 
		  		   and bankcardtwo = #{bankcardtwo} 
		  		   and bankpaytwodate = #{bankpaytwodate} 
		  		   and fultranstwo = #{fultranstwo} 
		  		   and fulpaytwodate = #{fulpaytwodate} 
		  		   and acbalance = #{acbalance} 


				   and receipt = #{receipt} 
				   and receipt in('有','预有') 



				 and receiptdate >= #{tstartDate} 
				 and receiptdate <= #{tendDate} 





					   and carrier like CONCAT('%',#{carrier},'%') 

		  		   and remark = #{remark} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from gc_kehu

	 and id = #{id} 

	 and bizdate >= #{startDate} 
	 and bizdate <= #{endDate} 


	 and forwunit = #{forwunit} 

		 and carnum like CONCAT('%',#{carnum},'%') 

	 and station = #{station} 
	 and tonnage = #{tonnage} 
	 and settletonnage = #{settletonnage} 
	 and adjusttonnage = #{adjusttonnage} 
	 and transcost = #{transcost} 
	 and inforfee = #{inforfee} 
	 and colmethod = #{colmethod} 
	 and transfee = #{transfee} 
	 and riskcues = #{riskcues} 
	 and fulltrans = #{fulltrans} 
	 and fulpaydate = #{fulpaydate} 
	 and bankcard = #{bankcard} 
	 and bankpaydate = #{bankpaydate} 
	 and bankcardtwo = #{bankcardtwo} 
	 and bankpaytwodate = #{bankpaytwodate} 
	 and fultranstwo = #{fultranstwo} 
	 and fulpaytwodate = #{fulpaytwodate} 
	 and acbalance = #{acbalance} 


	 and receipt = #{receipt} 
	 and receipt in('有','预有') 



	 and receiptdate >= #{tstartDate} 
	 and receiptdate <= #{tendDate} 





		 and carrier like CONCAT('%',#{carrier},'%') 

	 and remark = #{remark}

select sum(acbalance) as acbalance,sum(fulltrans) as fulltrans,sum(bankcard) as bankcard, sum(xianjin) as xianjin,sum(shiyou) as shiyou,sum(shihua) as shihua,sum(jingbo) as jingbo, sum(qichu) as qichu from (
-- 当天回款金额(transfee) select sum(transfee) as acbalance,0 as fulltrans,0 as bankcard,0 as xianjin,0 as shiyou,0 as shihua,0 as jingbo,0 as qichu from gc_kehu where receipt in ('预有','有')
and receiptdate >= #{startDate} and receiptdate <= #{endDate}
union all
select 0 as acbalance,sum(fulltrans) as fulltrans,sum(bankcard) as bankcard, sum(xianjin) as xianjin,sum(shiyou) as shiyou,sum(shihua) as shihua,sum(jingbo) as jingbo, 0 as qichu from ( -- 第一次付款金额 select 0 as acbalance, sum(case when pay_type = '运满满' then fulltrans else 0 end) as fulltrans, sum(case when pay_type = '农行卡' then fulltrans else 0 end) as bankcard, sum(case when pay_type = '现金' then fulltrans else 0 end) as xianjin, sum(case when pay_type = '百通石油' then fulltrans else 0 end) as shiyou, sum(case when pay_type = '百通石化' then fulltrans else 0 end) as shihua, sum(case when pay_type = '百通京博' then fulltrans else 0 end) as jingbo, 0 as qichu from gc_kehu where receipt in ('预有','有')
and fulpaydate >= #{startDate} and fulpaydate <= #{endDate} group by pay_type
union all
-- 第二次付款金额 select 0 as acbalance, sum(case when forwunit = '运满满' then bankcard else 0 end) as fulltrans, sum(case when forwunit = '农行卡' then bankcard else 0 end) as bankcard, sum(case when forwunit = '现金' then bankcard else 0 end) as xianjin, sum(case when forwunit = '百通石油' then bankcard else 0 end) as shiyou, sum(case when forwunit = '百通石化' then bankcard else 0 end) as shihua, sum(case when forwunit = '百通京博' then bankcard else 0 end) as jingbo, 0 as qichu from gc_kehu where receipt in ('预有','有') and bankpaydate >= #{startDate} and bankpaydate <= #{endDate}
group by forwunit
) t
union all
-- 期初余额（等于当月的所有余额累计） select 0 as acbalance,0 as fulltrans,0 as bankcard,0 as xianjin,0 as shiyou,0 as shihua,0 as jingbo, (transfee - fulltrans - bankcard) as qichu from ( select sum(transfee) as transfee, sum(case when fulpaydate>= #{startDate}and fulpaydate< #{endDate} then fulltrans else 0 end) as fulltrans, sum(case when bankpaydate>= #{startDate} and bankpaydate< #{endDate} then bankcard else 0 end) as bankcard from gc_kehu where receipt in ('预有','有') and receiptdate >= #{startDate} and receiptdate <= #{endDate} ) t
) t
