mapper.select:
		select `id`,`bizdate`,`forwunit`,`carnum`,`steelnum`,`specs`,`packnum`,
		       `tonnage`,`baseprice`,`coefficient`,`tranrate`,`trancost`,
		       `arrivstation`,`payer`,`inforfee`,`remark`,`issueoffice`,
		       `aminvoice`,`custompay`,`taxdatepay` ,order_no,pid,dept_id,updated_name
		from gc_siji



			 and bizdate >= #{startDate} 
			 and bizdate <= #{endDate} 



				 and inforfee like CONCAT('%',#{inforfee},'%') 



				 and carnum like CONCAT('%',#{carnum},'%') 


				 and arrivstation like CONCAT('%',#{arrivstation},'%') 



				and dept_id = #{deptId}






                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from gc_siji



	 and bizdate >= #{startDate} 
	 and bizdate <= #{endDate} 



		 and inforfee like CONCAT('%',#{inforfee},'%') 


		 and carnum like CONCAT('%',#{carnum},'%') 


		 and arrivstation like CONCAT('%',#{arrivstation},'%') 



		and dept_id = #{deptId}
