mapper.select:
		select `id`,`bizdate`,`forwunit`,`carnum`,`steelnum`,`specs`,`packnum`,
		       `tonnage`,`baseprice`,`coefficient`,`tranrate`,`trancost`,
		       `arrivstation`,`payer`,`inforfee`,`remark`,`issueoffice`,
		       `aminvoice`,`custompay`,`taxdatepay` ,order_no,pid
		from gc_siji

		  		   and id = #{id} 

			 and bizdate >= #{startDate} 
			 and bizdate <= #{endDate} 



				 and forwunit like CONCAT('%',#{forwunit},'%') 


				 and carnum like CONCAT('%',#{carnum},'%') 

			 and steelnum = #{steelnum} 
		  		   and specs = #{specs} 
		  		   and packnum = #{packnum} 
		  		   and tonnage = #{tonnage} 
		  		   and baseprice = #{baseprice} 
		  		   and coefficient = #{coefficient} 
		  		   and tranrate = #{tranrate} 
		  		   and trancost = #{trancost} 

					   and arrivstation like CONCAT('%',#{arrivstation},'%') 

		  		   and payer = #{payer} 
		  		   and inforfee = #{inforfee} 
		  		   and remark = #{remark} 
		  		   and issueoffice = #{issueoffice} 
		  		   and aminvoice = #{aminvoice} 
		  		   and custompay = #{custompay} 
		  		   and taxdatepay = #{taxdatepay} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

